(function($){'use strict';$.fn.init_prbar=function(){return this.each(function(index,el){var percentage=$(el).attr('data-percentage')==undefined?0:$(el).attr('data-percentage');var title=$(el).attr('data-title')==undefined?'':$(el).attr('data-title');var ShowProgressCount=$(el).attr('data-show-progress')==undefined?true:$(el).attr('data-show-progress');var duration=$(el).attr('data-duration')==undefined?3000:$(el).attr('data-duration');var fillBackgroundColor=$(el).attr('data-fcolor')==undefined?'#fed136':$(el).attr('data-fcolor');var backgroundColor=$(el).attr('data-bcolor')==undefined?'#EEEEEE':$(el).attr('data-bcolor');var radius=$(el).attr('data-radius')==undefined?'0px':$(el).attr('data-radius');var height=$(el).attr('data-height')==undefined?'10px':$(el).attr('data-height');var width=$(el).attr('data-width')==undefined?'100%':$(el).attr('data-width');$(el).html('<div class="row">'+'<div class="col-lg-12">'+'<p class="text-uppercase title_bar font-weight-bold"><span class="percentCount"></span></p>'+'</div>'+'<div class="col-lg-12">'+'<div class="progressbar">'+'<div class="proggress">'+'</div>'+'</div>'+'</div>'+'</div>');var progressFill=$(el).find('.proggress');var progressBar=$(el).find('.progressbar');var titleBar=$(el).find('.title_bar');progressFill.css({backgroundColor:fillBackgroundColor,height:height,borderRadius:radius});progressBar.css({width:width,backgroundColor:backgroundColor,borderRadius:radius});titleBar.prepend(title);progressFill.animate({width:percentage+"%"},{step:function(x){if(ShowProgressCount){$(el).find(".percentCount").text(Math.round(x)+"%");}},duration:duration});});}})(jQuery);